Trigger.script:
while dreadnought.actions.size > 0 do local action = dreadnought.heap.head(dreadnought.actions)
action.postCommand()
if action.name == "hull" then dreadnought.heap.extract(dreadnought.actions) else break end end

local action = dreadnought.heap.head(dreadnought.actions)
if action.name == "vortex" then dreadnought.heap.extract(dreadnought.actions)
action.postCommand() end

local action = dreadnought.heap.head(dreadnought.actions)
if action.name == "shockwave" then dreadnought.heap.extract(dreadnought.actions)
action.postCommand() end

ScriptGroup.script:
dreadnought = dreadnought or {
	version = "3.0",
	aetherbeastsTotal = 0,
	currentModule = "",
	balance = true,
	locked = false,
	skills = {},
	reset = {},
	setup = {}
}

dreadnought.pilot = dreadnought.pilot or {
	shockwaveTimers = {},
	siphonCalloutCount = 0,
	aetherbeastsPresent = 0,
	currentPosition = 1,
	flags = {
		shocked = false,
		siphonCallout = false
	},

	paths = {}
}

function dreadnought.reset.pilot()
	return {
		aetherbeastsTotal = 0,
		currentModule = "",

		pilot = {
			calloutTimer = 0,
			aetherbeastsPresent = 0,
			currentPosition = 1,
			flags = {
				shocked = false,
				siphonCallout = false
			},

			paths = {}
		}
	}
end

Script.script:
function dreadnought.findNextPosition(currentPosition) local positions = { currentPosition = 0, nextPosition = dreadnought.pilot.paths[currentPosition].next }
if currentPosition == 1 or currentPosition == 2 then positions.currentPosition = currentPosition + 1 else positions.currentPosition = 2 end
return positions end

function dreadnought.findHome(currentPosition)
	local positions = {
		currentPosition = 0,
		nextPosition = dreadnought.pilot.paths[currentPosition].home
	}

	if positions.nextPosition ~= "" then
		positions.currentPosition = 1
	end

	return positions
end

ScriptGroup.script:
dreadnought.empath = dreadnought.empath or {
	hull = {
		current = 0,
		max = 0
	},

	modules = {
		chair = "",
		grid = "",
		turrets = {},
		collectors = {},
		orb = "",
		cube = ""
	},

	skills = {},

	flags = {
		checkSlivven = false
	},

	slivvensFound = 0,
	inspectedModule = ""
}

function dreadnought.reset.empath()
	return {
		aetherbeastsTotal = 0,
		currentModule = "",
		actions = dreadnought.heap.new(),

		empath = {
			hull = {
				current = 0,
				max = 0
			},

			modules = {
				chair = "",
				grid = "",
				turrets = {},
				collectors = {},
				orb = "",
				cube = ""
			},

			skills = dreadnought.empath.skills,

			flags = {
				checkSlivven = false
			},

			slivvensFound = 0,
			inspectedModule = ""
		}
	}
end

dreadnought.combateer = dreadnought.combateer or {
	skills = {},
	flags = {
		canVortex = false
	}
}

function dreadnought.reset.combateer()
	return {
		aetherbeastsTotal = 0,
		currentModule = "",
		actions = dreadnought.heap.new(),

		combateer = {
			skills = dreadnought.combateer.skills,
			flags = {
				canVortex = false
			}
		}
	}
end

Script.script:
local empathSkills = { Empathy = true, Armada = true, Interlink = true, Modules = true, Trade = true, Analyze = true, Distribution = true, Steward = true, Astralsight = true, Purser = true, Scan = true, FirstMate = true, Resonance = true }
local combateerSkills = { Battle = true, Targeting = true, Bombard = true, Rupture = true, Bosun = true, TurretSiphon = true, SecondMate = true, Shock = true, Clarionblast = true }
function dreadnought.skillsListener() if dreadnought.currentModule == "grid" then for i,skill in ipairs(gmcp.Char.Skills.List.list) do if empathSkills[skill] then dreadnought.empath.skills[skill] = true end end elseif dreadnought.currentModule == "turret" then for i,skill in ipairs(gmcp.Char.Skills.List.list) do if combateerSkills[skill] then dreadnought.combateer.skills[skill] = true end end end end
registerAnonymousEventHandler("gmcp.Char.Skills.List", "dreadnought.skillsListener")
